package gic.gradle.app;

import gic.gradle.list.LinkedList;

import static gic.gradle.utilities.StringUtils.join;
import static gic.gradle.utilities.StringUtils.split;
import static gic.gradle.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
